// Merge Sorted Array
// https://oj.leetcode.com/problems/merge-sorted-array/
// 

class Solution {
public:
    void merge(int A[], int m, int B[], int n) {
        int i = 0, j = 0;
        while (i < m && j < n) {
            if (A[i] <= B[j]) {
                ++i;
            } else {
                insertArray(A, m++, B[j++], i++);
            }
        }
        while (j < n) {
            insertArray(A, m++, B[j++], i++);
        }
    }
    
private:
    // 在数组A的pos位置处，插入元素e
    // 0 <= pos <= m
    void insertArray(int A[], int m, int e, int pos) {
        for (int i = m - 1; i >= pos; --i) {
            A[i + 1] = A[i];
        }
        A[pos] = e;
    }
};

// 
// Given two sorted integer arrays 
// A and 
// B, merge 
// B into 
// A as one sorted array. 
// Note: 
// You may assume that 
// A has enough space (size that is greater or equal to m + n) to hold additional elements from 
// B. 
// The number of elements initialized in 
// A and 
// B are m and n respectively.
// 
